        :root {
            --body_background: #F1F1F1;
            --blocks_background: #E4E4E4;
            --button_background: #ff6d16;
            --icons: #157EFB;
            --button_text: #FFFFFF;
            --link: #259DDE;
            --blocks_text: #FFFFFF;
        }
        body {
            position: relative;
            overflow-x: hidden;
            margin: 0;
            padding: 0;
            width: 100%;
            color: #444;
